import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import './Login.css';
import { withStyles } from '@material-ui/core/styles';
import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';

const styles = theme => ({
    cssLabel: {
        color : '#1a73e8'
    },
    cssOutlinedInput: {
        '&$cssFocused $notchedOutline': {
        borderColor: `#1a73e8 !important`,
        }
    },
    cssFocused: {
        borderWidth: '1px',
        borderColor: '#1a73e8 !important'
    },
    notchedOutline: {
        borderWidth: '1px',
        borderColor: 'grey !important'
    },
    checkBox: {
        fontSize: '13px',
    },
});

export class LoginPage extends Component {

    state = {
        email: '',
        password: '',
        hidden: true
    }

    //  Constructor to bind function with state
    constructor(props) {
        super(props)
        this.handleLogin = this.handleLogin.bind(this)
        this.onChange = this.onChange.bind(this)
        this.handlePasswordToogle = this.handlePasswordToogle.bind(this)
    } 

    //Handle Password Visibility
    handlePasswordToogle() {
        console.log('working')
        this.setState({ hidden: !this.state.hidden });
    }

    //  Hadles change of text in textfield 
    onChange(e){
        this.setState({ [e.target.name]: e.target.value });
    }

    //  Handles Login
    handleLogin(e){
        e.preventDefault();
        console.log(this.state.email, this.state.password)
    }

    render() {
        const { classes } = this.props;
        return (
            <div class="box">
                <Typography className="headerName" component="h1" variant="h5">
                    Sign in
                </Typography>
                <Typography className="headerDesc" variant="subtitle1">
                    Use your Books Account
                </Typography>
                <form className="loginForm">
                    <TextField 
                        variant="outlined" margin="normal" required autoFocus onChange={this.onChange} 
                        InputLabelProps={{ shrink: true, classes: { classes: { root: classes.cssLabel, focused: classes.cssFocused }}}} 
                        InputProps={{classes: { root: classes.cssOutlinedInput, focused: classes.cssFocused, notchedOutline: classes.notchedOutline }}}
                        placeholder="Email" fullWidth id="email" label="Email" 
                        name="email" autoComplete="email"/>
                    <TextField 
                        variant="outlined" margin="normal" placeholder="Password" onChange={this.onChange}
                        InputLabelProps={{ shrink: true, classes: { classes: { root: classes.cssLabel, focused: classes.cssFocused }}}} 
                        InputProps={{classes: { root: classes.cssOutlinedInput, focused: classes.cssFocused, notchedOutline: classes.notchedOutline }}}
                        required fullWidth name="password" label="Password" InputLabelProps={{ shrink: true }}
                        type={this.state.hidden ? "password" : "text" } id="password" autoComplete="current-password"/>
                    <Grid container spacing={3}>
                        <Grid item xs={6}>
                            <Button className="forgotButton" color="primary">Forgot Password</Button>                        
                        </Grid>
                        <Grid item xs={6}>
                            <FormControlLabel
                                value="top"
                                classes={{ label: classes.checkBox }}
                                control={<Checkbox color="primary" size="small" checkBox={this.state.hidden} onChange={this.handlePasswordToogle}/>}
                                label="Show password"
                                labelPlacement="right"/>
                        </Grid>
                    </Grid>
                    <Button className="loginButton" variant="contained" onClick={this.handleLogin}>Login</Button>                        
                </form>
                <Typography className="orClass" variant="subtitle1">
                    or
                </Typography>
                <Button className="createButton" color="primary">Don't have an account.  Create one</Button>
            </div>
        );
    }
}

export default withStyles(styles)(LoginPage);
