import React, {Component} from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import {withRouter} from 'react-router'
import { withStyles } from '@material-ui/core/styles';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';

function createData(name, email, isActive, UserType, CreatedAt) {
    return { name, email, isActive, UserType, CreatedAt };
  }

  const rows = [
    createData('Joan Louji', 'sjlouji10@gmail.com', 'false', 'Admin', 'Jan 2020'),
    createData('Joan Louji', 'sjlouji10@gmail.com', 'false', 'Admin', 'Jan 2020'),
    createData('Joan Louji', 'sjlouji10@gmail.com', 'false', 'Admin', 'Jan 2020'),
    createData('Joan Louji', 'sjlouji10@gmail.com', 'false', 'Admin', 'Jan 2020'),
    createData('Joan Louji', 'sjlouji10@gmail.com', 'false', 'Admin', 'Jan 2020'),
    createData('Joan Louji', 'sjlouji10@gmail.com', 'false', 'Admin', 'Jan 2020'),
    createData('Joan Louji', 'sjlouji10@gmail.com', 'false', 'Admin', 'Jan 2020'),
    createData('Joan Louji', 'sjlouji10@gmail.com', 'false', 'Admin', 'Jan 2020'),
  ];

  const useStyles = ({
    table: {
      minWidth: 650,
    },
  });

export class UserTable extends Component {
    render() {
        const { classes } = this.props;
        return(
            <div {...this.props.style ? {style: this.props.style} : ''}>
                    <TableContainer style={{ background: '#fafafa' }}>
                        <div style={{ padding: '10px' }}>
                            <Typography style={{ fontSize: '24px', fontFamily: 'Roboto,RobotoDraft,Helvetica,Arial,sans-serif' }} color="textPrimary">Users</Typography>
                        </div>
                        <Table className={classes.table} aria-label="simple table">
                            <TableHead>
                            <TableRow>
                                <TableCell>Name</TableCell>
                                <TableCell >Email</TableCell>
                                <TableCell align="right">Active Member</TableCell>
                                <TableCell align="right">User Type</TableCell>
                                <TableCell align="right">Created At</TableCell>
                            </TableRow>
                            </TableHead>
                            <TableBody>
                            {rows.map((row) => (
                                <TableRow key={row.name}>
                                <TableCell component="th" scope="row">
                                    {row.name}
                                </TableCell>
                                <TableCell >{row.email}</TableCell>
                                <TableCell align="right">{row.isActive}</TableCell>
                                <TableCell align="right">{row.UserType}</TableCell>
                                <TableCell align="right">{row.CreatedAt}</TableCell>
                                </TableRow>
                            ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
            </div>
        );
    }
}

export default (withStyles(useStyles))(withRouter(UserTable))
