import React, {Component} from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Logo from '../Logo/Logo';
import Profile from './Profile';
import Search from './Search';
import Button from '@material-ui/core/Button';
import ClickAwayListener from '@material-ui/core/ClickAwayListener';
import Grow from '@material-ui/core/Grow';
import Paper from '@material-ui/core/Paper';
import Popper from '@material-ui/core/Popper';
import MenuItem from '@material-ui/core/MenuItem';
import MenuList from '@material-ui/core/MenuList';
export class Sidebar extends Component {
    state = {
        open: false,
    }

    constructor(props){
        super(props);
        this.handleToggle = this.handleToggle.bind(this)
    }

    handleToggle() { 
        this.setState({
            open: !this.state.open,
        })
    }
    render() {
        const colors = ['#C62828', '#AD1457', '#6A1B9A', '#4527A0', '#283593', '#1565C0', '#D84315', '#424242'];
        const randomElement = colors[Math.floor(Math.random() * colors.length)];
        return(
            <div>
                <AppBar position="fixed" color="inherit" elevation="0" style={{ borderBottom: '1px solid #cecece' }}>
                    <Toolbar>
                        {/* Logo */}
                        <Logo />

                        {/* Search bar */}
                        <Search />

                        {/* Profile Icon */}
                        <Profile 
                            style={{ float: 'right', width: '35px', height: '35px', background: randomElement}}
                            value = {this.props.username ? this.props.username.slice(0,1).toUpperCase() : ''}
                        />
                        <div>
                            <Button
                                aria-controls={this.state.open ? 'menu-list-grow' : undefined}
                                aria-haspopup="true"
                                onClick={this.handleToggle}
                                >
                                Toggle Menu Grow
                            </Button>
                            <Popper open={this.state.open} role={undefined} transition disablePortal>
                                {({ TransitionProps, placement }) => (
                                    <Grow
                                    {...TransitionProps}
                                    style={{ transformOrigin: placement ===  'center top' }}
                                    >
                                    <Paper>
                                        <ClickAwayListener onClickAway={this.handleToggle}>
                                        <MenuList autoFocusItem={this.state.open} id="menu-list-grow">
                                            <MenuItem onClick={this.handleToggle}>Profile</MenuItem>
                                            <MenuItem onClick={this.handleToggle}>My account</MenuItem>
                                            <MenuItem onClick={this.handleToggle}>Logout</MenuItem>
                                        </MenuList>
                                        </ClickAwayListener>
                                    </Paper>
                                    </Grow>
                                )}
                            </Popper>
                        </div>
                    </Toolbar>
                </AppBar>
            </div>
        );
    }
}

export default (Sidebar);
