import {
    USER_LIST_LOADING,
    USER_LIST_LOADED
  } from '../Action/types';

  const initialState = {
    isUserLoading: false,
    user: null,
  };

  export default function (state = initialState ,action) {
    switch (action.type) {

      case USER_LIST_LOADED:
        return {
          ...state,
          isUserLoading: false,
          user: action.payload.users,
        };
      default:
        return state;
    }
  }
