            <FormControl type="text" 
            placeholder="Search" 
            className="mr-sm-2" 
            name="search_query"
            id="search_query"
            style={{ width: '450px',  marginRight: '0px !important', marginLeft: '200px',  }} 
            onChange={this.onChange}
            value={search_query}
            onKeyPress={event => {
              if (event.key === 'Enter') {
                this.onSubmit()
              }
            }}
            />
            <Button style={{ backgroundColor: '#3f8f88' }} ><SearchIcon /></Button>
         </Form>
      </div>
    )
  }
}


export default (withRouter(Search));
