import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/core/styles';
import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';

const styles = theme => ({
    cssLabel: {
        color : '#1a73e8'
    },
    cssOutlinedInput: {
        '&$cssFocused $notchedOutline': {
            borderColor: `#1a73e8 !important`,
        }
    },
    cssErrorOutlinedInput: {
        '&$cssFocused $notchedOutline': {
            borderColor: `red !important`,
        }
    },
    cssFocused: {
        borderWidth: '1px',
        borderColor: '#1a73e8 !important'
    },
    notchedOutline: {
        borderWidth: '1px',
        borderColor: 'grey !important'
    },
    errorNotchedOutline: {
        borderWidth: '1px',
        borderColor: 'red !important'
    },
    checkBox: {
        fontSize: '13px',
    },
});

export class AuthField extends Component {

    // State
    state = {
        email: '',
        password: '',
        hidden: true,
        error: 'sadf'
    }

    //  Constructor to bind function with state
    constructor(props) {
        super(props)
        this.handleLogin = this.handleLogin.bind(this)
        this.onChange = this.onChange.bind(this)
        this.handlePasswordToogle = this.handlePasswordToogle.bind(this)
    } 

    //Handle Password Visibility
    handlePasswordToogle() {
        console.log('working')
        this.setState({ hidden: !this.state.hidden });
    }

    //  Hadles change of text in textfield 
    onChange(e){
        this.setState({ [e.target.name]: e.target.value });
    }

    //  Handles Login
    handleLogin(e){
        e.preventDefault();
        console.log(this.state.email, this.state.password)
    }

    render() {
        const { classes } = this.props;
        return (
            <div>
                <TextField 
                    variant="outlined" 
                    margin="normal" 
                    required 
                    autoFocus 
                    onChange={this.onChange} 
                    InputLabelProps={{ 
                        shrink: true, 
                        classes: { 
                            classes: { 
                                root: classes.cssLabel, 
                                focused: classes.cssFocused 
                            }
                        }
                    }} 
                    InputProps={{
                        classes: {   
                            root: this.state.error ? classes.cssErrorOutlinedInput : classes.cssOutlinedInput, 
                            focused: classes.cssFocused, 
                            notchedOutline: this.state.error ? classes.errorNotchedOutline : classes.notchedOutline 
                        }}}
                    placeholder={this.props.placeholder} 
                    fullWidth id={this.props.placeholder} 
                    label="Email" 
                    {...(this.state.error && {error: true, helperText: "hello world"})}
                    name="email" autoComplete="email"
                />
            </div>
        );
    }
}

export default withStyles(styles)(AuthField);
