import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/core/styles';
import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';

const styles = theme => ({
    cssLabel: {
        color : '#1a73e8'
    },
    cssOutlinedInput: {
        '&$cssFocused $notchedOutline': {
            borderColor: `#1a73e8 !important`,
        }
    },
    cssErrorOutlinedInput: {
        '&$cssFocused $notchedOutline': {
            borderColor: `red !important`,
        }
    },
    cssFocused: {
        borderWidth: '1px',
        borderColor: '#1a73e8 !important'
    },
    notchedOutline: {
        borderWidth: '1px',
        borderColor: 'grey !important'
    },
    errorNotchedOutline: {
        borderWidth: '1px',
        borderColor: 'red !important'
    },
    checkBox: {
        fontSize: '13px',
    },
});

export class AuthField extends Component {

    state  = {
        error: ''
    }

    //  Recieves new props. Primarily used for Error Handling
    componentWillReceiveProps(nextProps){
        if(nextProps.error){
            this.setState({
                error: nextProps.error
            })
        }
    }    
    render() {
        const { classes } = this.props;
        console.log(this.state.error)
        return (
            <div>
                <TextField 
                    variant="outlined" 
                    margin="normal" 
                    required 
                    {...this.props.name === 'email' ? 'autoFocus' : ''} 
                    onChange={this.props.onChange} 
                    value={this.props.value}
                    InputLabelProps={{ 
                        shrink: true, 
                        classes: { 
                            classes: { 
                                root: classes.cssLabel, 
                                focused: classes.cssFocused 
                            }
                        }
                    }} 
                    InputProps={{
                        classes: {   
                            root: this.state.error ? classes.cssErrorOutlinedInput : classes.cssOutlinedInput, 
                            focused: classes.cssFocused, 
                            notchedOutline: this.state.error ? classes.errorNotchedOutline : classes.notchedOutline 
                        }}}
                    placeholder={this.props.placeholder} 
                    fullWidth id={this.props.placeholder} 
                    label={this.props.label}  
                    {...(this.state.error && {error: true, helperText: this.state.error})}
                    name={this.props.name}
                />
            </div>
        );
    }
}

export default withStyles(styles)(AuthField);
