import React, {Component} from 'react';
import Divider from '@material-ui/core/Divider';
import Drawer from '@material-ui/core/Drawer';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import MailIcon from '@material-ui/icons/Mail';
import MenuIcon from '@material-ui/icons/Menu';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles, useTheme, withStyles } from '@material-ui/core/styles';
const drawerWidth = 240;

const useStyles = ((theme) => ({
    drawer: {
      width: drawerWidth,
      flexShrink: 0,
      zIndex: 0,
    },
    drawerPaper: {
      width: drawerWidth,
      zIndex: 0,
      borderRight: '0'
    },
    drawerContainer: {
      overflow: 'auto',
    },
    isActive: {
        backgroundColor: '#e8f0fe',
        color: '#1967d2',
        borderRadius: '0px 50px 50px 0px'
    },
    listItem: {
        backgroundColor: '#e8f0fe',
        color: '#1967d2',
        borderRadius: '0px 50px 50px 0px'
    },
  }));

export class Sidebar extends Component {
    render() {
        const { classes } = this.props;
        const categories = [
            { id: 'Home', icon: <InboxIcon /> ,to: '/', isActive: false},
            { id: 'Blog', icon: <InboxIcon /> ,to: '/blog', isActive: false},
            { id: 'Events', icon: <InboxIcon /> ,to: '/events', isActive: false},
            { id: 'Calender', icon: <InboxIcon /> ,to: '/calender', isActive: true},
          ];
        return(
            <Drawer className={classes.drawer} variant="permanent" classes={{ paper: classes.drawerPaper }}>
                <div style={{ marginTop: '70px' }}>
                    <List>
                        {categories.map((data)=> {
                            return (
                                <ListItem button key="100" className={data.isActive ? classes.isActive : classes.isActive}>
                                    <ListItemIcon><MailIcon style={{ color: '#1967d2' }}/></ListItemIcon>
                                    <ListItemText primary={data.id} style={{ fontFamily: "Sen, sans-serif" }}/>
                                </ListItem>
                            )
                        })}
                    </List>
                </div>
            </Drawer>
        );
    }
}

export default (withStyles(useStyles))(Sidebar);
