import React, {Component} from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Logo from '../Logo/Logo';
import Profile from './Profile';
import Search from './Search';
import Button from '@material-ui/core/Button';
import ClickAwayListener from '@material-ui/core/ClickAwayListener';
import Grow from '@material-ui/core/Grow';
import Paper from '@material-ui/core/Paper';
import Popper from '@material-ui/core/Popper';
import MenuItem from '@material-ui/core/MenuItem';
import MenuList from '@material-ui/core/MenuList';
import { withStyles } from '@material-ui/core/styles';
import Menu from '@material-ui/core/Menu';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import DraftsIcon from '@material-ui/icons/Drafts';
import SendIcon from '@material-ui/icons/Send';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import Avatar from '@material-ui/core/Avatar';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { Divider } from '@material-ui/core';
import SupervisorAccountIcon from '@material-ui/icons/SupervisorAccount';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';

const StyledMenu = withStyles({
    paper: {
      border: '1px solid #d3d4d5',
    },
  })((props) => (
    <Menu
      elevation={0}
      getContentAnchorEl={null}
      anchorOrigin={{
        vertical: 'bottom',
        horizontal: 'right',
      }}
      transformOrigin={{
        vertical: 'bottom',
        horizontal: 'bottom',
      }}
      {...props}
    />
  ));

  const StyledMenuItem = withStyles((theme) => ({
    root: {
      '&:focus': {
        backgroundColor: theme.palette.primary.main,
        '& .MuiListItemIcon-root, & .MuiListItemText-primary': {
          color: theme.palette.common.white,
        },
      },
    },
  }))(MenuItem);
export class Sidebar extends Component {
    state = {
        open: false,
        anchorEl: null,
    }

    constructor(props){
        super(props);
        this.handleToggle = this.handleToggle.bind(this)
        this.myRef = React.createRef();
    }

    handleToggle(event) { 
        this.setState({
            anchorEl: event.currentTarget,
            open: !this.state.open,
        })
    }
    render() {
        const colors = ['#C62828', '#AD1457', '#6A1B9A', '#4527A0', '#283593', '#1565C0', '#D84315', '#424242'];
        const randomElement = colors[Math.floor(Math.random() * colors.length)];
        return(
            <div>
                <AppBar position="fixed" color="inherit" elevation="0" style={{ borderBottom: '1px solid #cecece' }}>
                    <Toolbar>
                        {/* Logo */}
                        <Logo />

                        {/* Search bar */}
                        <Search />

                        {/* Profile Icon */}
                        <div onClick={(event)=>this.handleToggle(event)} style={{ cursor: 'pointer' }}>
                            <Profile 
                                style={{ float: 'right', width: '35px', height: '35px', background: randomElement}}
                                value = {this.props.username ? this.props.username.slice(0,1).toUpperCase() : ''}

                            />
                        </div>
                        <div>
                            <Popper open={this.state.open} anchorEl={this.state.anchorEl} transition disablePortal>
                                    <Paper style={{ width: '250px', border: '1px solid #cecece'}} elevation="0">
                                        <div style={{ padding: '25px' }}>
                                            <Grid container spacing={3}>
                                                <Grid xs={3}> 
                                                    <Avatar style={{ width: '45px', height: '45px' }}>{this.props.username ? this.props.username.slice(0,1).toUpperCase() : ''}</Avatar>
                                                </Grid>
                                                <Grid xs={6}>
                                                    <Typography variant="subtitle1">
                                                        {this.props.username ? this.props.username: ''}
                                                    </Typography>
                                                    <Typography variant="body2">
                                                        {this.props.email ? this.props.email: ''}
                                                    </Typography>
                                                </Grid>
                                            </Grid>
                                        </div>
                                        <Divider />
                                        <ClickAwayListener onClickAway={(event)=>this.handleToggle(event)}>
                                        <MenuList autoFocusItem={this.state.open} id="menu-list-grow">
                                            <MenuItem onClick={(event)=>this.handleToggle(event)}>
                                                <ListItemIcon>
                                                    <SupervisorAccountIcon fontSize="small" />
                                                </ListItemIcon>
                                                <Typography variant="button" display="block" gutterBottom>
                                                    Profile
                                                </Typography>
                                            </MenuItem>
                                            <MenuItem onClick={(event)=>this.handleToggle(event)}><ExitToAppIcon />Logout</MenuItem>
                                        </MenuList>
                                        </ClickAwayListener>
                                    </Paper>
                            </Popper>
                        </div>
                    </Toolbar>
                </AppBar>
            </div>
        );
    }
}

export default (Sidebar);
