import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/core/styles';
import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import AuthField from '../../../Components/TextField/AuthField';
import './Register.css';

const styles = theme => ({
    cssFocused: {
        borderWidth: '1px',
        borderColor: '#1a73e8 !important'
    },
    notchedOutline: {
        borderWidth: '1px',
        borderColor: 'grey !important'
    },
    checkBox: {
        fontSize: '13px',
    },
});

export class RegisterPage extends Component {

    // State
    state = {
        email: '',
        firstName: '',
        lastName: '',
        mobile: '',
        password: '',
        confirmPassword: '',
        hidden: true,
        emailError: '',
        passwordError: '',
        firstNameError: '',
        lastNameError: '',
        confirmError: '',
        mobileError: ''
    }

    //  Constructor to bind function with state
    constructor(props) {
        super(props)
        this.handleLogin = this.handleLogin.bind(this)
        this.onChange = this.onChange.bind(this)
        this.handlePasswordToogle = this.handlePasswordToogle.bind(this)
        this.flushError = this.flushError.bind(this)
    } 

    //Handle Password Visibility
    handlePasswordToogle() {
        this.setState({ hidden: !this.state.hidden });
    }

    // Makes Error null
    flushError() {
        this.setState({
            emailError: '',
            passwordError: ''
        })
    }

    //  Hadles change of text in textfield 
    onChange(e){
        this.setState({ [e.target.name]: e.target.value });
    }

    //  Handles Login
    handleLogin(e){
        e.preventDefault();
        if (!this.state.email && !this.state.password && !this.state.firstName && !this.state.lastName) {return this.setState({ emailError: 'Enter a Email address', passwordError: 'Enter your password' })} else {this.flushError()}
        if (!this.state.email) {return this.setState({ emailError: 'Enter a Email address' })} else {this.flushError()}
        if (!this.state.password) {return this.setState({ passwordError: 'Enter your password' })} else {this.flushError()}
        this.flushError();
        console.log(this.state.email, this.state.password)
    }

    render() {
        const { classes } = this.props;
        return (
            <div class="box">
                <Typography className="headerName" component="h1" variant="h5">
                    Sign up
                </Typography>
                <Typography className="headerDesc" variant="subtitle1">
                    Create your Books Account
                </Typography>
                <form className="loginForm">
                    <Grid container xs={12}>
                        <Grid item xs={6} style={{padding: '5px'}}> 
                            <AuthField 
                                name="firstName" 
                                placeholder="First Name" 
                                margin='small'
                                label="First Name" 
                                error = {this.state.firstNameError} 
                                onChange={this.onChange} 
                                value={this.state.firstName}/>
                        </Grid>
                        <Grid item xs={6} style={{padding: '5px'}}>
                            <AuthField 
                                name="lastName" 
                                placeholder="Last Name" 
                                margin='small'
                                label="Last Name" 
                                error = {this.state.lastNameError} 
                                onChange={this.onChange} 
                                value={this.state.lastName}/>                            
                        </Grid>
                        <Grid item xs = {12} style={{padding: '5px', marginTop: '10px'}}>
                            <AuthField 
                                name="email" 
                                placeholder="Email" 
                                margin='small'
                                label="Email" 
                                error = {this.state.emailError} 
                                onChange={this.onChange} 
                                value={this.state.email}/>
                        </Grid>
                        <Grid item xs = {12} style={{padding: '5px'}}>
                            <AuthField 
                                name="password" 
                                placeholder="Password" 
                                label="Password"    
                                error = {this.state.passwordError} 
                                onChange={this.onChange} 
                                value={this.state.password} 
                                hidden={this.state.hidden}/>
                        </Grid>
                    </Grid>
                    <Grid container spacing={3}>
                        <Grid item xs={6}>
                            <FormControlLabel
                                value="top"
                                classes={{ label: classes.checkBox }}
                                control={<Checkbox color="primary" size="small" checkBox={this.state.hidden} onChange={this.handlePasswordToogle}/>}
                                label="Show password"
                                labelPlacement="right"/>
                            <Button className="forgotButton" color="primary">Forgot Password</Button>                        
                        </Grid>
                        <Grid item xs={6}>
                            <Button className="loginButton" variant="contained" onClick={this.handleLogin}>Login</Button>                        
                        </Grid>
                    </Grid>
                </form>
                <Typography className="orClass" style={{ marginTop: '15px' }} variant="subtitle1">
                    or
                </Typography>
                <Button className="createButton" color="primary">Don't have an account.  Create one</Button>
            </div>
        );
    }
}

export default withStyles(styles)(RegisterPage);
