import React, { Component } from 'react'
import Breadcrumbs from '@material-ui/core/Breadcrumbs';
import Link from '@material-ui/core/Link';
import Typography from '@material-ui/core/Typography';
import {withRouter} from 'react-router'
import { Grid } from '@material-ui/core';
import PeopleAltIcon from '@material-ui/icons/PeopleAlt';
import UserTable from '../../../Components/Table/UserTable';
export class UsersPage extends Component {

    state = {
        column: [
            { name: "name", label: "Name" },
            { name: "email", label: "Email" },
            { name: "is_active", label: "Is Active" },
            { name: "user_type", label: "User Type" },
            { name: "created_at", label: "Created At" },
        ],
        row: [
            { name: "Joe James", email: "sjlouji10@gmail.com", is_active: "true", created_at: "Jan 2021", user_type: 'Super Admin' },
            { name: "Joan", email: "rionLouji@gmail.com", is_active: "false", created_at: "Febb 2020", user_type: 'Admin' },
            { name: "Louji", email: "louji@gmail.com", is_active: "false", created_at: "Jan 2019", user_type: 'Super Admin' },
            { name: "Rion", email: "joan@louji.com", is_active: "false", created_at: "Aprl 2023", user_type: 'Admin' },
            { name: "Salette", email: "sjlouji10@gmail.com", is_active: "true", created_at: "Jan 2021", user_type: 'Admin' },
        ],
    }

    // Handle Navigation
    handleNav(data) {
        this.props.history.push(data)
    }

    render() {
        return(
            <div>
                {/* Page Name */}
                <Grid container direction="row" alignItems="center">
                    <Grid item>
                        <PeopleAltIcon  style={{ color: '#5f6368', fontSize: '20px' }}/>
                    </Grid>
                    <Grid item style={{ marginLeft: '5px' }}>
                        <Typography style={{ fontFamily: 'Roboto,RobotoDraft,Helvetica,Arial,sans-serif', fontSize: '15px', fontWeight: '500', color: '#5f6368' }}>Users</Typography>
                    </Grid>
                </Grid>
                {/* Breadcrumbs */}
                <Breadcrumbs aria-label="breadcrumb" style={{ fontSize: '13px' }}>
                    <Link color="inherit" style={{ cursor: 'pointer' }} onClick={() => this.handleNav('/')}>
                        Home
                    </Link>
                    <Link color="inherit" style={{ cursor: 'pointer' }} onClick={() => this.handleNav('/')}>
                        Users
                    </Link>
                    <Typography style={{ fontSize: '13px' }} color="textPrimary">List Users</Typography>
                </Breadcrumbs>
                {/* Table */}
                <UserTable 
                    style={{ padding: '50px' }}
                    tabName = "Users"
                    columns={this.state.column}
                    row={this.state.row}/>
            </div>
        );
    }
}

export default withRouter(UsersPage)
