import React, { Component,Suspense } from 'react';
import { connect } from 'react-redux';
import { renderRoutes } from 'react-router-config';
import AppsIcon from '@material-ui/icons/Apps';
import MenuBookIcon from '@material-ui/icons/MenuBook';
import {withRouter} from 'react-router'
import { logout } from '../../Store/Action/auth'
import  PrivateRoute  from '../../Route/PrivateRoute';
import { createBrowserHistory } from 'history';
import Sidebar from '../../Components/Sidebar/Sidebar'
import Appbar from '../../Components/Appbar/Appbar'
import InboxIcon from '@material-ui/icons/MoveToInbox';
import PeopleOutlineIcon from '@material-ui/icons/PeopleOutline';
import BlockIcon from '@material-ui/icons/Block';
import VerifiedUserIcon from '@material-ui/icons/VerifiedUser';
import SupervisedUserCircleIcon from '@material-ui/icons/SupervisedUserCircle';
import LocalActivityIcon from '@material-ui/icons/LocalActivity';
const history = createBrowserHistory();

export class HomePage extends Component {
    state = {
        sidebarItem: [
            { id: 'Users', icon: <PeopleOutlineIcon /> ,to: '/', isActive: true},
            { id: 'Black List', icon: <BlockIcon /> ,to: '/blog', isActive: false},
            { id: 'Permissions', icon: <VerifiedUserIcon /> ,to: '/events', isActive: false},
            { id: 'Activity Monitoring', icon: <InboxIcon /> ,to: '/calender', isActive: false},
            { id: 'SSO Users', icon: <SupervisedUserCircleIcon /> ,to: '/calender', isActive: false},
            { id: 'SSO User Activity', icon: <LocalActivityIcon /> ,to: '/calender', isActive: false},
          ],
    }
    render() {
        return(
            <div>
                <Appbar
                    username={this.props.user ? this.props.user.firstName : ''}
                    />
                <Sidebar item={this.state.sidebarItem}/>
            </div>
        );
    }
}

const mapStateToProps = (state) => ({
    isAuthenticated: state.auth.isAuthenticated,
    user: state.auth.user
  });

export default connect(mapStateToProps, {logout})(withRouter(HomePage));
