import React, { Component,Suspense } from 'react';
import { connect } from 'react-redux';
import { renderRoutes } from 'react-router-config';
import AppsIcon from '@material-ui/icons/Apps';
import MenuBookIcon from '@material-ui/icons/MenuBook';
import {withRouter} from 'react-router'
import { logout } from '../../Store/Action/auth'
import  PrivateRoute  from '../../Route/PrivateRoute';
import { createBrowserHistory } from 'history';
import Sidebar from '../../Components/Sidebar/Sidebar'
import Appbar from '../../Components/Appbar/Appbar'
import InboxIcon from '@material-ui/icons/MoveToInbox';
import PeopleOutlineIcon from '@material-ui/icons/PeopleOutline';
import BlockIcon from '@material-ui/icons/Block';
import VerifiedUserIcon from '@material-ui/icons/VerifiedUser';
import SupervisedUserCircleIcon from '@material-ui/icons/SupervisedUserCircle';
import LocalActivityIcon from '@material-ui/icons/LocalActivity';
import Typography from '@material-ui/core/Typography';

const history = createBrowserHistory();
export class HomePage extends Component {
    state = {
        sidebarItem: [
            { id: 'Users', icon: <PeopleOutlineIcon /> ,to: '/', isActive: this.props.location.pathname === '/' ? true : false },
            { id: 'Black List', icon: <BlockIcon /> ,to: '/blacklist', isActive: this.props.location.pathname === '/blacklist' ? true : false},
            { id: 'Permissions', icon: <VerifiedUserIcon /> ,to: '/permissions', isActive: this.props.location.pathname === '/permissions' ? true : false},
            { id: 'Activity Monitoring', icon: <InboxIcon /> ,to: '/logs', isActive: this.props.location.pathname === '/logs' ? true : false},
            { id: 'SSO Users', icon: <SupervisedUserCircleIcon /> ,to: '/sso/users', isActive: this.props.location.pathname === '/sso/users' ? true : false},
            { id: 'SSO User Activity', icon: <LocalActivityIcon /> ,to: '/sso/logs', isActive: this.props.location.pathname === '/sso/logs' ? true : false},
          ],
    }

    componentWillReceiveProps(nextProps) {
        console.log(this.state.sidebarItem['to'])
        console.log(nextProps.location.pathname)
        console.log(this.state.sidebarItem['to'] === nextProps.location.pathname)
    }
    render() {
        if(!this.props.isAuthenticated) {
            this.props.history.push('/auth/login')
        }
        return(
            <div>
                {/* Appbar */}
                <Appbar
                    username={this.props.user ? this.props.user.firstName : ''}
                    />
                {/* Sidebar */}
                <Sidebar item={this.state.sidebarItem}/>
                {/* Content */}
                <main style={{ padding: '100px', marginLeft: '170px'}}>
                    <Suspense fallback='Loading'>
                        {renderRoutes(this.props.route.routes)}
                    </Suspense>
                </main>
            </div>
        );
    }
}

const mapStateToProps = (state) => ({
    isAuthenticated: state.auth.isAuthenticated,
    user: state.auth.user
  });

export default connect(mapStateToProps, {logout})(withRouter(HomePage));
