import React, {Component} from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Logo from '../Logo/Logo';
import Profile from './Profile';
import Search from './Search';
import Button from '@material-ui/core/Button';
import ClickAwayListener from '@material-ui/core/ClickAwayListener';
import Grow from '@material-ui/core/Grow';
import Paper from '@material-ui/core/Paper';
import Popper from '@material-ui/core/Popper';
import MenuItem from '@material-ui/core/MenuItem';
import MenuList from '@material-ui/core/MenuList';
export class Sidebar extends Component {
    state = {
        open: false,
    }

    constructor(props){
        super(props);
        this.handleToggle = this.handleToggle.bind(this)
    }

    handleToggle() { 
        this.setState({
            open: !this.state.open,
        })
    }
    render() {
        const colors = ['#C62828', '#AD1457', '#6A1B9A', '#4527A0', '#283593', '#1565C0', '#D84315', '#424242'];
        const randomElement = colors[Math.floor(Math.random() * colors.length)];
        const StyledMenuItem = withStyles((theme) => ({
            root: {
              '&:focus': {
                backgroundColor: theme.palette.primary.main,
                '& .MuiListItemIcon-root, & .MuiListItemText-primary': {
                  color: theme.palette.common.white,
                },
              },
            },
          }))(MenuItem);
        return(
            <div>
                <AppBar position="fixed" color="inherit" elevation="0" style={{ borderBottom: '1px solid #cecece' }}>
                    <Toolbar>
                        {/* Logo */}
                        <Logo />

                        {/* Search bar */}
                        <Search />

                        {/* Profile Icon */}
                        {/* <div onClick={this.handleToggle} style={{ cursor: 'pointer' }}>
                            <Profile 
                                style={{ float: 'right', width: '35px', height: '35px', background: randomElement}}
                                value = {this.props.username ? this.props.username.slice(0,1).toUpperCase() : ''}

                            />
                        </div> */}
                        <div>
                            <Button
                                aria-controls="customized-menu"
                                aria-haspopup="true"
                                variant="contained"
                                color="primary"
                                onClick={handleClick}
                            >
                                Open Menu
                            </Button>
                            <StyledMenu
                                id="customized-menu"
                                anchorEl={anchorEl}
                                keepMounted
                                open={Boolean(anchorEl)}
                                onClose={handleClose}
                            >
                                <StyledMenuItem>
                                <ListItemIcon>
                                    <SendIcon fontSize="small" />
                                </ListItemIcon>
                                <ListItemText primary="Sent mail" />
                                </StyledMenuItem>
                                <StyledMenuItem>
                                <ListItemIcon>
                                    <DraftsIcon fontSize="small" />
                                </ListItemIcon>
                                <ListItemText primary="Drafts" />
                                </StyledMenuItem>
                                <StyledMenuItem>
                                <ListItemIcon>
                                    <InboxIcon fontSize="small" />
                                </ListItemIcon>
                                <ListItemText primary="Inbox" />
                                </StyledMenuItem>
                            </StyledMenu>
                        </div>
                    </Toolbar>
                </AppBar>
            </div>
        );
    }
}

export default (Sidebar);
