import {
    USER_LIST_LOADING,
    USER_LIST_LOADED,
    REGISTER_SUCCESS
  } from '../Action/types';

  const initialState = {
    isUserLoading: false,
    user: null,
  };

  export default function (state = initialState ,action) {
    switch (action.type) {
      case USER_LIST_LOADED:
        return {
          ...state,
          isUserLoading: false,
          user: action.payload.users,
        };
      case USER_LIST_LOADING:
        return {
          ...state,
          user: null,
          isUserLoading: true,
        };
      case REGISTER_SUCCESS:
        localStorage.setItem('token', action.payload.Token)
        return {
          ...state,
          user: action.payload.user,
          isAuthenticated: true,
          isLoading: false,
          authLoading: false
        };
      default:
        return state;
    }
  }
