import React, { Component, Fragment } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import './Login.css';
import { withStyles } from '@material-ui/core/styles';



const styles = theme => ({
    container: {
      display: 'flex',
      flexWrap: 'wrap',
    },
    textField: {
      marginLeft: theme.spacing.unit,
      marginRight: theme.spacing.unit,
      width: 200,
    },

    cssLabel: {
      color : 'green'
    },

    cssOutlinedInput: {
      '&$cssFocused $notchedOutline': {
        borderColor: `${theme.palette.primary.main} !important`,
      }
    },

    cssFocused: {
        borderColor: 'green !important'
    },

    notchedOutline: {
      borderWidth: '1px',
      borderColor: 'green !important'
    },

  });

export class LoginPage extends Component {
    render() {
        const { classes } = this.props;
        return (
            <div class="box">
                <Typography className="headerName" component="h1" variant="h5">
                    Sign in
                </Typography>
                <Typography className="headerDesc" variant="subtitle1">
                    Use your Books Account
                </Typography>
                <form className="loginForm">
                    <TextField 
                        variant="outlined" margin="normal" required 
                        InputLabelProps={{ 
                            shrink: true,
                            classes: {
                                classes: {
                                    root: classes.cssLabel,
                                    focused: classes.cssFocused,
                                  },
                              },
                        }} 
                        InputProps={{
                            classes: {
                              root: classes.cssOutlinedInput,
                              focused: classes.cssFocused,
                              notchedOutline: classes.notchedOutline,
                            },
                            inputMode: "numeric"
                          }}
                        placeholder="Email"
                        fullWidth id="email" label="Email" 
                        name="email" autoComplete="email"/>
                    <TextField 
                        variant="outlined" margin="normal" placeholder="Password"
                        required fullWidth name="password" label="Password" InputLabelProps={{ shrink: true }}
                        type="password" id="password" autoComplete="current-password"/>
                    <Grid container spacing={3}>
                        <Grid item xs={6}>
                            <Button className="forgotButton" color="primary">Forgot Password</Button>                        
                        </Grid>
                        <Grid item xs={6}>
                            <Button className="loginButton" variant="contained">Login</Button>                        
                        </Grid>
                    </Grid>
                </form>
                <Typography className="orClass" variant="subtitle1">
                    or
                </Typography>
                <Button className="createButton" color="primary">Don't have an account.  Create one</Button>
            </div>
        );
    }
}

export default withStyles(styles)(LoginPage);
